import React, { useEffect, useState } from 'react'
import RoundWrapper from '../../util/RoundWrapper'
import styles from './CommentCard.module.css'
import Logo from '../../../assets/logo.png'
import moment from 'moment'
import {CgMoreAlt} from 'react-icons/cg'
import CommentOption from './CommentOption'

const CommentCard = ({post, comment}) => {
    const [isShowOption, setShowOption] = useState(false);
    const [showMore, setShowMore] = useState(false);
    const [isLong, setIsLong] = useState(false);
    const [onReply, setOnReply] = useState(false);

    const test = 'Hello  this si a loreem tello thisHello  this sHello  this si a loreem tello thisHello  this sHello  this si a loreem tello thisHello  this sHello  this si a loreem tello thisHello  this si alorem tello thisHello  this si a lorem tello thisHello  this si a lorem tello this si  Hello this si a lorem text Hello this si a lorem tello this si s si a lorem text Hello this si a lorem text Hello this si a lorem text Hello this si a lorem text'
    
    useEffect(()=> {
        if(test.length > 250) {
            setIsLong(true);
            setShowMore(true);
        }
        else setIsLong(false);
    },[test])

    return (
        <div className={styles.cmt_wrapper}>
            <RoundWrapper diameter='50px' >
                <img src={comment.user.avatar} />
            </RoundWrapper>
            <div className={styles.content}>
                <div className={styles.name}>{comment.user.username}</div>
                {isLong ?
                `${test.slice(0,250)}...` : 
                test
                }
                {showMore ? <label>[hidden]</label> : <label>[show more]</label>}
                <div className={styles.option_btn}>
                    <CgMoreAlt/>
                </div>
            </div>
            <div className={styles.time}>{moment(post.createdAt).fromNow()}</div>

        </div>
    )
}

export default CommentCard
