import React, { useEffect, useState, useRef } from 'react'
import {useDispatch, useSelector} from 'react-redux'
import {Link} from 'react-router-dom'
import ReactDOM from 'react-dom';

import { updateComment } from '../../../store/actions/commentAction'

import CommentOption from './CommentOption'
import RoundWrapper from '../../util/RoundWrapper'

import {CgMoreAlt} from 'react-icons/cg'
import {GoReply} from 'react-icons/go'

import styles from './CommentCard.module.css'
import TextareaAutosize from 'react-textarea-autosize'
import moment from 'moment'

const CommentCard = ({post, comment, setOnReply}) => {
    const ref = useRef();
    const card = useRef(null);
    const dispatch = useDispatch();

    const dropdown = document.getElementById('dropdown_comment');

    const [isShowOption, setShowOption] = useState(false);
    const [body, setBody] = useState('');
    const [showMore, setShowMore] = useState(false);
    const [isLong, setIsLong] = useState(false);
    const [isEdit, setIsEdit] = useState(false);
    const [onSubmit, setOnSubmit] = useState(false);

    const {auth} = useSelector(state=>state);
    const posts = useSelector(state=>state.post.posts)

    useEffect(()=>{
        // Function to check if click outside the object
        const checkIfClickOutside = e => {
            if(isShowOption && ref.current &&!ref.current.contains(e.target)) setShowOption(false); 
        }
        // Set event 'when click' trigger
        document.addEventListener("mousedown", checkIfClickOutside)
        return () => {
            // Clean the event
            document.removeEventListener("mousedown", checkIfClickOutside)
        }
    },[isShowOption]);

    useEffect(()=>{
        if(onSubmit) submitHandler();
    },[onSubmit]);
    
    useEffect(()=>{
        setBody(comment.body);
        setOnReply(false);
    },[comment.body, isEdit])

    useEffect(()=> {
        if(body && body.length > 250) {
            setIsLong(true);
            setShowMore(true);
        }
        else setIsLong(false);
    },[body])

    const submitHandler = () => {
        const trimBody = body.trim();
        if(trimBody=='') return setOnSubmit(false);
        dispatch(updateComment(auth, trimBody, post, posts, comment));
        setIsEdit(false);
        setOnSubmit(false);
    }

    const checkHandler = (e) => {
        if(e.charCode == 13) {
            e.preventDefault();
            setOnSubmit(true);
        }
    }

    const replyHandler = () => {
        setOnReply(comment);
    }

    return (
        <div className={styles.cmt_wrapper}>
            <div className={styles.avatar}>
                <Link to={`profile/${comment.user._id}`} >
                    <RoundWrapper diameter={`${comment.reply ? '40' :'50'}px`} >
                        <img src={comment.user.avatar} />
                    </RoundWrapper>
                </Link>
            </div>
            <div ref={card} className={styles.content_wrapper}>
            <div style={{width: `${isEdit? 'auto' : 'fit-content'}`}} className={styles.content}>
                <div className={styles.name}>{comment.user.username}</div>
                <div className={styles.text}>
                    {isEdit ? 
                    <TextareaAutosize value={body} className={styles.input} onKeyPress={checkHandler} spellCheck='false' onChange={e=>setBody(e.target.value)} placeholder='Write your comment here...' />
                    : 
                    isLong && showMore ?
                    `${body.slice(0,250)}... ` : 
                    body.trim()
                    }
                    {isLong && (showMore ? 
                    <label onClick={()=>setShowMore(false)} className={styles.toggle_text}>[show more]</label> : 
                    <label onClick={()=>setShowMore(true)} className={styles.toggle_text}>[hidden]</label>
                    )}
                    {isEdit && <div onClick={()=>setIsEdit(false)} className={styles.cancel}>Cancel</div>}
                </div>
                {(auth.user._id === post.user._id || auth.user._id ===  comment.user._id) && 
                <div ref={ref} style={{opacity: `${isShowOption ? '1' : '0'}`}} onClick={()=>setShowOption(!isShowOption)} className={styles.option_btn}>
                    <CgMoreAlt/>
                </div>
                }
                {!isEdit && <div onClick={replyHandler} className={styles.reply}><GoReply className={styles.icon} /></div>}
            </div>
            </div>           
            <div className={styles.time}>{moment(comment.createdAt).fromNow().replace(/(ago)+/g , '')}</div>
            {ReactDOM.createPortal(<CommentOption  right={card.current && card.current.getBoundingClientRect().x + card.current.getBoundingClientRect().width} setIsEdit={setIsEdit} comment={comment} post={post} bottom={ref.current && ref.current.getBoundingClientRect().y} isShow={isShowOption} />, dropdown)}
        </div>
    )
}

export default CommentCard
