import React, { useState, useRef, useEffect } from 'react'
import { useSelector } from 'react-redux'
import { Link } from 'react-router-dom'

import Search from '../../../header/Search'
import UserCard from '../../../util/UserCard'
import { searchFriend } from '../../../../store/actions/searchAction'

import { motion, AnimatePresence } from "framer-motion"

import Spinner from '../../../../assets/Spinner.png'
import styles from './SearchResult.module.css'

const SearchResult = ({setSelectedUsers}) => {
    const ref = useRef();

    const [users, setUsers] = useState([]);
    const [isShow, setIsShow] = useState(false);

    const {search} = useSelector(state=>state);

    useEffect(()=>{
        const checkIfClickOutside = e => {
            if(isShow && ref.current && !ref.current.contains(e.target)) setIsShow(false); 
        }
        document.addEventListener("mousedown", checkIfClickOutside)
        return () => {
            document.removeEventListener("mousedown", checkIfClickOutside)
        }
    },[isShow]);

    useEffect(()=>{
        setUsers(search.friendUsers)
    },[search.friendUsers])

    return (
        <div className={styles.container} ref={ref}>
            <Search searchAction={searchFriend} setIsShow={setIsShow} setUsers={setUsers} />
            <AnimatePresence>
                {isShow && 
                <motion.div
                initial={{maxHeight: 0, overflow: 'hidden'}}
                animate={{maxHeight: 400, overflow: 'overlay'}}
                transition={{duration: .5}}
                exit={{ maxHeight: 0, overflow: 'hidden'}}
                className={styles.results}>            
                    {search.loading ?
                    <div className={styles.spinner_wrapper}><img src={Spinner} alt='spinner' /></div>
                    :
                    users.length==0 ? 
                    <div className={styles.no_result}>No searching result...</div>
                    :
                    users.map(user => 
                        <div key={user._id} className={styles.item_wrapper}>
                            <Link to={`/profile/${user._id}`} className={styles.item}>
                                <UserCard height='2rem' avatar={user.avatar} username={user.username} />
                            </Link>             
                            {/* search result hiển thị như cũ, chỉ thay mỗi phần Link thành onClick để thực hiện chức năng khác nhau */}
                        </div>
                    )}
                </motion.div>}
            </AnimatePresence>
        </div>
    )
}

export default SearchResult
