import {postAPI, deleteAPI, getAPI, patchAPI} from '../../apis/Axios'

export const createNotice = (auth, msg, socket) => async (dispatch) => {
  try {
    const res = await postAPI(`notice`, msg, auth.token);

    socket.emit('createNotice', {
      ...res.data.notice,
      user: {
        username: auth.user.username,
        avatar: auth.user.avatar
      }
    })

  }   catch (err) {
      dispatch({type: 'ALERT', payload: err.response.data.msg});
  }
}

export const deleteNotice = (auth, msg, notices) => async (dispatch) => {
  try {
    const res = await deleteAPI(`notice/${msg.id}?url=${msg.url}`, auth.token);

    if(notices.length <= 0) return;
    
    let newNotices = notices.filter(n=>n.id!==msg.id);
    
    dispatch({type: 'DELETE_NOTICE', payload: newNotices});


  } catch(err) {
    dispatch({type: 'ALERT', payload: err.response.data.msg});
  }
}

export const deleteNotices = (auth) => async (dispatch) => {
  try {
    const res = await deleteAPI(`notice`, auth.token);
    dispatch({type: 'DELETE_NOTICE', payload: []});

  } catch(err) {
    dispatch({type: 'ALERT', payload: err.response.data.msg});
  }
}

export const unreadNotice = (auth, msg, notices) => async (dispatch) => {
  try {
    const res = await patchAPI(`notice/unread/${msg.id}`, null, auth.token);

    const newNotices = notices.map(n=>n.id===res.data.notice.id?res.data.notice:n);
    
    dispatch({type: 'UPDATE_NOTICE' , payload: newNotices});

  } catch(err) {
    dispatch({type: 'ALERT', payload: err.response.data.msg});
  }
}

export const readNotice = (auth, msg, notices) => async (dispatch) => {
  try {
    const res = await patchAPI(`notice/${msg.id}`, null, auth.token);

    const newNotices = notices.map(n=>n.id===res.data.notice.id?res.data.notice:n);
    
    dispatch({type: 'UPDATE_NOTICE' , payload: newNotices});

  } catch(err) {
    dispatch({type: 'ALERT', payload: err.response.data.msg});
  }
}

export const readNotices = (auth) => async (dispatch) => {
  try {
    const res = await patchAPI('notice', null, auth.token);


  } catch(err) {
    dispatch({type: 'ALERT', payload: err.response.data.msg});
  }
}

export const getNotices = (auth) => async (dispatch) => {
  try {
    const res = await getAPI(`notice`, auth.token);
    dispatch({type: 'GET_NOTICES' , payload: res.data.notices})

  } catch(err) {
    dispatch({type: 'ALERT', payload: err.response.data})
  }
}
