import React from 'react';
import styles from './SlideForm.module.css';
import RoundButton from '../Util/RoundButton';
import {useDispatch,useSelector} from 'react-redux';
import {slideActions} from '../../store/index';

export default function SlideForm() {
    let BODY_DATA= {
        title: 'new here?',
        text1: 'You can create here and post your experience here.',
        text2: 'Share your post increase your view.',
        valueBtn: 'Sign up'
    };

    const isLogin = useSelector(state => state.slide.isLogin);

    const [data,setData] = useState(BODY_DATA);

    useEffect(()=>{
        setData({
            title: 'already have one?',
            text: 'Go login and check your daily posts on newfeed',
            valueBtn: 'Sign in'
        });
    },[])

    const dispatch = useDispatch();

    const toggleSlide = () => {
        console.log(isLogin);
        dispatch(slideActions.toggleSlide());
    }

    return (
        <div className={`${styles.slide} ${!isLogin && styles.slide_animation}`}>
            <div className={styles.content_wrapper}>
                <div className={styles.container}>
                    <div className={styles.title}> {BODY_DATA.title} </div>
                    <div className={styles.text}> {BODY_DATA.text1} <br/> {BODY_DATA.text2} </div>
                    <RoundButton Func={toggleSlide}  color={'#487fec'} bgcolor={'white'} type={'text'} value={BODY_DATA.valueBtn} />
                </div>
            </div>
        </div>
    )
}
