import React, {useEffect} from 'react'
import { useDispatch, useSelector } from 'react-redux'
import {Link} from 'react-router-dom'

import {getSuggestion} from '../../../store/actions/suggestionAction'

import UserCard from '../../util/UserCard'

import styles from './FriendSuggestion.module.css'

const FriendSuggestion = () => {
    const dispatch = useDispatch();
    const {auth, suggestion} = useSelector(state=>state);

    const findMutual = (me, diu) => {
        let res =[];
        me.forEach(item=>{
            res[item] = diu.filter(d=>d===item).length
        })
        console.log(res);
    }
    
    return (
        <div className={styles.container}>
            {suggestion.users.map((user,index)=>(
                <div className={styles.item_wrapper}>
                    <Link className={styles.item} to={`/profile/${user._id}`}>
                        <UserCard key={index} avatar={user.avatar} username={user.username} />
                        {findMutual(auth.user.following, user.following )}
                    </Link>
                </div>
                
            ))}
            
        </div>
    )
}

export default FriendSuggestion
