import React from 'react'
import styles from './DropdownCard.module.css'
import { CSSTransition } from "react-transition-group"

const DropdownCard = (props) => {
    return (
        <CSSTransition
            timeout={500}
            classNames={{
                enter: styles.enter2,
                enterActive: styles.enter_active2,
                exit: styles.exit2,
                exitActive: styles.exit_active2,
            }}
            unmountOnExit
            in={props.isShow}
        >
        <div style={{bottom: props.bottom, right: props.right, width: props.width}} className={styles.container}>
            {props.children}
        </div>
        </CSSTransition>
        
    )
}

export default DropdownCard
