import './App.css';
import {BrowserRouter as Router, Route, Switch} from 'react-router-dom';
import Auth from './views/Auth/Auth';
import Home from './views/Home/Home';
import ProtectedRoute from './components/customRouter/ProtectedRoute';
import {refreshToken} from './store/actions/authAction';
import {useEffect, useState} from 'react';
import {useSelector, useDispatch} from 'react-redux';
import Alert from './components/alert/Alert';
import React from 'react';
import { PageRender } from './components/customRouter/PageRender';

function App() {
  const dispatch = useDispatch();

  const [isLoading, setLoading] = useState(false);
  const  auth  = useSelector(state => state.auth);

  useEffect(()=>{
    setLoading(true);
    dispatch(refreshToken());
    setLoading(false);

  },[dispatch]);

  // useEffect(()=>{
  //   if(auth.token){
  //     dispatch()
  //   }
  // })

  return (

  <Router>
    <Alert />
    <Switch>
      {isLoading && <Route exact path='/' component={auth.token ? Home : Auth} />}

      <ProtectedRoute exact path='/:page' component={PageRender} />
      <ProtectedRoute exact path='/:page/:id' component={PageRender} />
    </Switch>
  </Router>
  )

}
export default App;
