import React, { useState } from 'react';
import {useDispatch, useSelector} from 'react-redux';
import styles from './Search.module.css';
import {AiOutlineSearch} from 'react-icons/ai';
import { getAPI } from '../../apis/Axios';
import UserCard from './UserCard';

const Search = () => {
    const dispatch = useDispatch();
    const [search,setSearch] = useState('');
    const [users,setUsers] = useState([]);
    const auth = useSelector(state=>state.auth);

    const submitHandler = async (e) => {
        e.preventDefault();

        if(!search) return;
        if(auth.token && search){
            try {
                const res = await getAPI(`user/search?username=${search}`, auth.token);
                console.log(res);
                setUsers(res.data.users);

            } catch (err) {
                dispatch({
                type: 'ALERT', payload: {error: err.response.data.msg}
            })
            }   
        }
    }

    const inputHandler = e => setSearch(e.target.value);
    const resetHandler = () => {

    }

    return (
        <form onSubmit={submitHandler} className={styles.search_bar}>
            <div className={styles.input_wrapper}>
                <label className={styles.icon_wrapper} htmlFor="seacrh"><AiOutlineSearch className={styles.icon}/></label>
                <input autoComplete="off" onChange={inputHandler} value={search}  className={styles.input} type="text" name="seacrh" id="seacrh" placeholder="Search for people, jobs, companies and more..."/>
                <button style={{display: 'none'}} type="submit" value="" ></button>
            </div>
            {search && (
                <div className={styles.results}>
                    <ul className={styles.list_items}>
                        <UserCard/>
                        <UserCard/>
                    </ul>
                </div>
            )}   
        </form>
    )
}

export default Search
