import axios from 'axios';
import store from '../store/index';
import {authActions} from '../store/slices/auth';

const postLogin = async (bodyFormData) => {
    try {
        const res = await axios.post('http://localhost:5000/api/auth/login',bodyFormData,{
            headers: {'content-type': 'multipart/form-data'}
        });
        if(res.status === 200){
            localStorage.setItem('TOKEN',res.data.accessToken);
        }
        await getLogin();
        return res;
    } catch(err) {
        return err.response.data;
    }
}

const getLogin = async () => {
    const state = store.getState();
    axios.defaults.headers.common['Authorization'] = `Bearer ${localStorage.getItem('TOKEN')}`;
    try {
        const res = await axios.get('http://localhost:5000/api/auth/login');
        if(res.status === 200) store.dispatch(authActions.login());
    } catch(err) {
        localStorage.removeItem('TOKEN');
        delete axios.defaults.headers.common['Authorization'];
        store.dispatch(authActions.logout());
        return err.message;
    }
}

export {postLogin};
