import React, { useState } from 'react';
import styles from './Status.module.css';
import {MdFormatQuote, MdPhotoCamera} from 'react-icons/md';
import {RiShareBoxLine} from 'react-icons/ri';
import {ImAttachment} from 'react-icons/im';
import RoundWrapper from '../../components/util/RoundWrapper';
import Card from '../util/Card';
import { useDispatch, useSelector } from 'react-redux';
import {createPost} from '../../store/actions/postAction';

const Status = () => {
    const dispatch = useDispatch();

    const auth = useSelector(state=>state.auth);

    const [form,setForm] = useState({
        body: '',
        image: ''
    });

    const inputHandler = (e) => {
        setForm({body: e.target.value});
    }

    const submitHandler = async e => {
        e.preventDefault();

        if(!form) return;
        if(auth.token && form){
            dispatch(createPost(form,auth));
        }
        try {

        } catch(err) {
            dispatch({type: 'ALERT', payload: {error: err.response.data.msg}})
        }
    }
    return (
        <div className={styles.container}>
            <ul className={styles.list_items}>
                <li className={styles.item}>
                    <RoundWrapper diameter='45px' bgcolor='#EBB35E'>
                        <MdFormatQuote />
                    </RoundWrapper>
                    <div className={styles.text}>Write a quote</div>
                </li>
                <li className={styles.item}>
                    <RoundWrapper diameter='45px' bgcolor='#D498E9'>
                        <MdPhotoCamera />
                    </RoundWrapper>
                    <div className={styles.text}>Upload a photo</div>
                </li>
                <li className={styles.item}>
                    <RoundWrapper diameter='45px' bgcolor='#7BCDD2'>
                        <RiShareBoxLine />
                    </RoundWrapper>
                    <div className={styles.text}>Share a post</div>
                </li>
            </ul>
            <Card width={'100'} height={'65'}>
                <form onSubmit={submitHandler} action="" method="post" className={styles.form}>
                    <textarea onChange={inputHandler} value={form.body}  className={styles.textarea}  spellcheck="false" type="text" name="body" id="" placeholder="What's on your mind?" />
                    <div className={styles.submit_wrapper}>
                        <div className={styles.attach_wrapper}>
                            <div className={styles.attach}>
                                <div className={styles.file}>
                                    <input className={styles.file_input} type="file" name="file" id="file" />
                                    <label className={styles.label} for="file"><ImAttachment/></label>
                                    </div>
                                <div className={styles.image}>
                                    <input className={styles.file_input} type="file" name="image" id="image" />
                                    <label className={styles.label} for="image"><MdPhotoCamera/></label>
                                </div>
                            </div>                            
                        </div>
                        <button className={styles.submit} type="submit">POST►</button>
                    </div>
                </form>
            </Card>
            
        </div>
    )
}

export default Status
