import React, { useEffect, useState } from 'react'
import ReactDOM from 'react-dom'
import {useParams} from 'react-router-dom'
import styles from './Profile.module.css'
import Header from '../../components/header/Header'
import RoundWrapper from '../../components/util/RoundWrapper'
import { useDispatch, useSelector } from 'react-redux'
import { getUser } from '../../store/actions/profileAction'
import EditModal from '../../components/modal/EditModal'
import FollowBtn from '../../components/profile/FollowBtn'
import EditBtn from '../../components/profile/EditBtn'
import Modal from '../../components/util/Modal'
import PostCard from '../../components/post/PostCard'
import FollowBar from '../../components/profile/FollowBar'

const Profile = () => {   
    const dispatch = useDispatch();
    const portal = document.getElementById('portal');
    
    const {id} = useParams();
    const {auth,profile} = useSelector(state=> state);

    const [userData, setUserData] = useState({});
    const [isMe, setIsMe] = useState(false);
    const [isEdit, setIsEdit] = useState(false);

    useEffect(() => {
        if(profile.user._id === auth.user._id) setIsMe(true);
        else setIsMe(false);
    }, [auth, profile]);

    useEffect(() => {
        dispatch(getUser(auth,id));
    },[auth, dispatch, id])

    useEffect(()=>{
        if(id === auth.user._id) setUserData(auth.user);
        else setUserData(profile.user);
    },[profile]);

    return (
        <>
            {isEdit && ReactDOM.createPortal(
            <Modal setIsEdit={setIsEdit} width='50%' height='80%' >
                <EditModal setIsEdit={setIsEdit} />
            </Modal>
            , portal)}
            <Header/>
            <div className={styles.profile_wrapper}>
                <div className={styles.leftside_bar}>
                    <FollowBar profile={profile} />
                </div>
                <div className={styles.container}>
                    <div className={styles.profile}>
                        <div style={{backgroundImage: `url('${profile.user.background}')`}} className={styles.user_bg_image}>
                            <div className={styles.user_avatar_image}>
                                <RoundWrapper margin='0' diameter='250px'>
                                    <img src={profile.user.avatar} alt='avatar' />
                                </RoundWrapper>
                            </div>
                        </div>
                        <div className={styles.information}>
                            <div className={styles.introduce}>
                                <div className={styles.name}>{profile.user.username}</div>
                                <div className={styles.bios}>{profile.user.bio}</div>
                            </div>
                            {isMe ? 
                                <EditBtn setIsEdit={setIsEdit} /> :
                                <div className={styles.follow_btn}>
                                    <FollowBtn  user={profile.user} />
                                </div>
                            }                      
                        </div>
                        {profile.posts && profile.posts.map(post=>
                            <PostCard key={post._id} post={post} />
                        )}
                    </div>
                </div>
            </div>
        </>   
    )
}

export default Profile
