import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import {Link} from 'react-router-dom'

import UserCard from '../../util/UserCard'
import Card from '../../../components/util/Card'
import FollowBtn from '../../profile/FollowBtn'

import {AiOutlineReload} from 'react-icons/ai'

import { motion } from "framer-motion"
import Spinner from '../../../assets/Spinner.png'
import styles from './FriendSuggestion.module.css'

const FriendSuggestion = () => {
    const dispatch = useDispatch();
    const {suggestion, auth} = useSelector(state=>state);
    const [suggestShow, setSuggestShow] = useState([]);
    const [showNumber, setShowNumber] = useState(5);
    useEffect(()=>{
        dispatch(getNotices(auth));

    },[auth, dispatch])

    useEffect(()=>{
        setSuggestShow(suggestion.users.slice(showNumber-5,showNumber))
    },[showNumber, suggestion.users])

    const reloadSuggestion = () => {
        if(showNumber>=suggestion.users.length) {
            setShowNumber(5);
            return;
        } 
        setShowNumber(showNumber+5)
    }
     
    return (
        <Card width={'100%'} mg='20px 0'>
            <div className={styles.container}>
                <div className={styles.header}>
                    <div className={styles.title}>People you may know</div>
                    <motion.div
                    onClick={reloadSuggestion}
                    className={styles.reload}
                    whileHover={{rotate: 200, scale: 1.2}}
                    >
                        <AiOutlineReload/>
                    </motion.div>
                </div>
                {suggestion.loading?
                <div className={styles.spinner_wrapper}><img src={Spinner} alt='spinner' /></div>
                :
                <div className={styles.suggestion_wrapper}>
                    {suggestShow.length>0 && suggestShow.map((user,index)=>(
                        <div className={styles.item_wrapper}>
                            <Link className={styles.item} to={`/profile/${user._id}`}>
                                <UserCard height='3rem' color='#64BDF1' key={index} avatar={user.avatar} username={user.username} />
                            </Link>
                            <div className={styles.btn_wrapper}>
                                <FollowBtn user={user}/>
                            </div>
                        </div>
                    ))}
                </div>
                }            
            </div>        
        </Card>
        
    )
}

export default FriendSuggestion
