import React, { useEffect, useState } from 'react'
import {useParams , Redirect} from 'react-router'
import { useDispatch, useSelector } from 'react-redux'
import ReactDOM from 'react-dom'

import { getUser } from '../../store/actions/profileAction'
import { useMediaQuery } from 'react-responsive'
import { motion, AnimatePresence } from "framer-motion"

import Header from '../../components/header/Header'
import FollowBtn from '../../components/profile/FollowBtn'
import EditBtn from '../../components/profile/EditBtn'
import FollowBar from '../../components/profile/FollowBar'
import InfiniteList from '../../components/util/InfiniteList'
import PostCard from '../../components/post/PostCard'

import {IoInformation} from 'react-icons/io5'
import {IoMdGrid} from 'react-icons/io'
import {GoFileMedia} from 'react-icons/go'
import {FaUser} from 'react-icons/fa'
import {GiPhone} from 'react-icons/gi'
import {HiHome} from 'react-icons/hi'

import Avatar from '../../components/util/Avatar'
import Spinner from '../../assets/Spinner.png'

import styles from './Profile.module.css'
import GridGallery from '../../components/profile/Tab/GridGallery'
import FriendCard from '../../components/profile/Tab/FriendCard'
import MediaCard from '../../components/profile/Tab/MediaCard'
import Modal from '../../components/util/Modal'
import FollowModal from '../../components/modal/FollowModal'

const Profile = () => {   
    const dispatch = useDispatch()
    const {id} = useParams()

    useEffect(() => {
        dispatch({type:'GET_USER', payload: {}})
        dispatch({type:'GET_POSTS', payload: []})
        
    }, [id])

    const {auth, profile, post} = useSelector(state=> state)

    const [isMe, setIsMe] = useState(false)
    const [isFollower, setIsFollower] = useState(true)
    const [followers, setFollowers] = useState([])
    const [following, setFollowing] = useState([])
    const [media, setMedia] = useState([])
    const [activeTab, setActiveTab] = useState(0)
    const [showFollow, setShowFollow] = useState(false)

    useEffect(() => {
        dispatch(getUser(auth, id))

    },[auth.user._id, dispatch, id])

    useEffect(()=>{
        !post.post && history.push('/pagenotfound')

    },[post.post])

    useEffect(() => {
        let media = []
        post.posts.map(e => {
            if(e.media.length > 0){
                media.push(<MediaCard post={e} />)
            }
        })
        
        setMedia(media)

    }, [post.posts])

    const isDesktop = useMediaQuery({ query: '(min-width: 1224px)' })

    useEffect(() => {
        if(profile.loading || Object.keys(profile.user).length === 0) return
        let tempFollowers =  profile.user.followers.slice(0, 6)
        let tempFollowing =  profile.user.following.slice(0, 6)

        let output = []

        tempFollowers.map(e=>{
            output.push(<FriendCard user={e} />)
        })

        setFollowers(output)
        output = []

        tempFollowing.map(e=>{
            output.push(<FriendCard user={e} />)
        })

        setFollowing(output)

    }, [profile])

    useEffect(() => {
        if(profile.user._id === auth.user._id) setIsMe(true);
        else setIsMe(false)

    }, [auth.user._id, profile.user._id])

    const tabs = [
        {icon: <IoMdGrid/>, label:'post'},
        {icon: <IoInformation/>, label:'about'},
        {icon: <GoFileMedia/>, label:'media'}
    ]

    return (
        <>
            {showFollow && ReactDOM.createPortal(
                <Modal setIsEdit={setShowFollow} width='100%' height='100%' >
                    <FollowModal title={isFollower ? 'Followers':'Following'} list={isFollower ? profile.user.followers : profile.user.following} />
                </Modal>
            , portal)}
            {(profile.user &&Object.keys(profile.user).length!==0) ? 
            <>
            <Header/>
            <div className={styles.profile_wrapper}>
                {isDesktop && <div className={styles.leftside}>
                    {!profile.loading && <FollowBar profile={profile} />}
                </div>}

                <div id='inf_user_posts' className={styles.container}>
                    <div className={styles.profile}>
                        <div style={{backgroundImage: `url('${profile.user.background}')`}} className={styles.background}></div>
                        {/* Information */}
                        <div className={styles.information}>
                            <div className={styles.avatar_wrapper}>
                                <div className={styles.avatar}>
                                    <Avatar src={profile.user.avatar} id={profile.user._id} diameter='15rem' />
                                </div>
                            </div>
                            <div className={styles.introduce}>
                                <div className={styles.name}>{!profile.loading && profile.user.username} </div>
                                <div className={styles.bios}>{!profile.loading && profile.user.bio} </div>
                            </div>
                            {isMe ? <EditBtn/> : (!profile.loading && <FollowBtn user={profile.user} />)}                      
                        </div>
                        {/* Follow */}
                        {!isDesktop &&
                        <div className={styles.follow}>
                            <div className={styles.heading}>
                                <div className={styles.title_wrapper}>
                                    <div className={styles.title}>{isFollower ? 'Followers' : 'Following'}</div>
                                    <div className={styles.count}>{!profile.loading && Object.keys(profile.user).length !== 0 && (isFollower ?  profile.user.followers.length : profile.user.following.length)}</div>
                                </div>
                                <button onClick={()=>setIsFollower(!isFollower)} className={styles.btn}>{isFollower ? 'Show following' : 'Show followers'}</button>
                            </div>
                            <div className={styles.body}>
                            <AnimatePresence>
                                {isFollower ? 
                                <motion.div
                                key='followers'
                                initial={{transform: 'translate(-100%,0)'}}
                                animate={{transform: 'translate(0%,0)'}}
                                className={styles.follower}>
                                    {!profile.loading && Object.keys(profile.user).length !== 0 && <GridGallery list={followers} />}
                                </motion.div>
                                :
                                <motion.div
                                key='following'
                                initial={{transform: 'translate(100%,0)'}}
                                animate={{transform: 'translate(0%,0)'}}
                                className={styles.following}>
                                    {!profile.loading && Object.keys(profile.user).length !== 0 && <GridGallery list={following} />}
                                </motion.div>
                                }
                            </AnimatePresence>
                            </div>
                            <div className={styles.footer}>
                                <button onClick={()=>setShowFollow(true)} className={styles.show_btn}>Show all</button>
                            </div>
                            
                        </div>}
                        {/* Tabs */}
                        <div className={styles.tabs_wrapper}>
                            <div className={styles.tabs}>
                                {tabs.map((tab, index) => (
                                    <div style={activeTab==index ? {borderTop:'1px solid #262626', margin: '-1px 2rem 0'} : {}} onClick={()=>setActiveTab(index)} key={index} className={styles.tab}>
                                        <div className={styles.icon_wrapper}>{tab.icon}</div>
                                    <div className={styles.text}>{tab.label}</div>
                                </div>)
                                )}
                            </div>
                            {activeTab == 0 && 
                            (profile.loading ? 
                                <div className={styles.spinner_wrapper}><img src={Spinner} alt='spinner' /></div>
                                : 
                                <InfiniteList action='GET_MORE_POSTS' api='post/user_posts' list={post.posts} elem='inf_user_posts' id={profile.user._id}>
                                    {post.posts.length>0 ? post.posts.map(e=>(
                                        <PostCard key={e._id} post={e} />
                                    ))
                                    :
                                    <div>No post available</div>}
                                </InfiniteList>)}
                            {activeTab == 1 &&
                                <div className={styles.about}>
                                    <div><FaUser/> Gender: {profile.user.gender}</div>
                                    <div><GiPhone/> Tel: {profile.user.tel}</div>
                                    <div><HiHome/> Address: {profile.user.address}</div>                                
                                </div>
                            }
                            {activeTab == 2 &&
                                <div>
                                    <GridGallery list={media} />
                                </div>
                            }
                        </div>
                    </div>
                </div>
            </div>
            </>
            :
            <Redirect to="/pagenotfound" />}
        </>   
    )
}

export default Profile
