import React, { useEffect,useRef,useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import {Link} from 'react-router-dom'

import {AiFillRightCircle} from 'react-icons/ai'
import {CgMoreAlt} from 'react-icons/cg'

import ReactBar from './ReactBar'
import { updatePost } from '../../store/actions/postAction'
import PostOption from './PostOption'
import PostCounter from './PostCounter'
import Comment from './comment/Comment'
import PostContent from './PostContent'
import InputComment from './comment/InputComment'
import Card from '../../components/util/Card'
import RoundWrapper from '../util/RoundWrapper'

import TextareaAutosize from 'react-textarea-autosize'
import moment from 'moment'
import styles from './PostCard.module.css'
import Avatar from '../util/Avatar'



const PostCard = ({post, isShared}) => {
    const dispatch = useDispatch();
    const ref = useRef();

    const [isEdit,setIsEdit] = useState(false);
    const [isShowOption, setShowOption] = useState(false);
    const [body,setBody] = useState();
    const [isComment, setIsComment] = useState(false);

    const {auth} = useSelector(state=>state);

    useEffect(() => {
        setBody(post.body);
    }, [post.body, isEdit]);

    useEffect(()=>{
        const checkIfClickOutside = e => {
            if(isShowOption && ref.current &&!ref.current.contains(e.target)) setShowOption(false); 
        }
        document.addEventListener("mousedown", checkIfClickOutside)
        return () => {
            document.removeEventListener("mousedown", checkIfClickOutside)
        }
    },[isShowOption]);

    const bodyChangeHandler = e => {
        setBody(e.target.value);
    }

    const submitHandler = e => {
        e.preventDefault();
        dispatch(updatePost(body, auth, post._id));
        setIsEdit(false);
    }

    return (
        <Card width='100%' bg='white' mg='0 0 4rem 0'>
            <div className={styles.content}>
                <div className={styles.heading}>
                    <div className={styles.avatar}>
                        <Avatar src={post.user.avatar} id={post.user._id} diameter='3rem' />
                    </div>
                    <div className={styles.name}>
                        <Link to={`/profile/${post.user._id}`} replace>
                            {post.user.username}
                        </Link>
                    </div>
                    <div className={styles.time}>{moment(post.createdAt).fromNow()}</div>
                </div>

                {isEdit ?
                <TextareaAutosize maxRows={8} spellCheck={false} type="text" name="body" className={styles.textarea} onChange={bodyChangeHandler} value={body} />
                : 
                <div className={styles.text}>{post.body}</div>
                }
                {isEdit &&
                <div className={styles.update}>
                    <div onClick={()=>setIsEdit(false)} className={`${styles.cancel} ${styles.update_btn}`}>Cancel</div>
                    <div onClick={submitHandler} className={`${styles.submit} ${styles.update_btn}`}>Submit</div> 
                </div>
                }

                {post.shareContent ? <PostCard post={post.shareContent} isShared={true} /> : <PostContent post={post} />}
            </div>
            {!isShared && <div style={{opacity: `${isShowOption ? '1' : '.5'}`}} ref={ref} onClick={()=>setShowOption(!isShowOption)} className={styles.option_btn}>
                <CgMoreAlt />
                <PostOption isShow={isShowOption} post={post} setIsEdit={setIsEdit} />
            </div>}
            {!isShared && <PostCounter post={post} />}
            {!isShared && <ReactBar isComment={isComment} setIsComment={setIsComment} post={post} />}
            {!isShared && <div className={styles.bottom_wrapper}>
                <Comment post={post} />
                {isComment && <InputComment post={post} />}             
            </div>}
            {!isShared && <div onClick={()=>setIsComment(true)} className={styles.reply}>
                <div className={styles.label}>Reply</div>
                <div className={styles.icon_wrapper}><AiFillRightCircle/></div>              
            </div>}
        </Card>
    )
}

export default PostCard
