import React from 'react'
import styles from './UserCard.module.css'

const UserCard = ({avatar, username, color, height}, props) => {
    return (
        <div className={styles.card}>
            <img style={{height: `${height}`}} className={styles.image} src={avatar} alt="avatar" />
            <div className={styles.text_wrapper}>
                <div style={{color: `${color? color:'black'}`}} className={styles.text}>
                    {username}
                </div>
            </div>
            {props.children}
        </div>
    )
}

export default UserCard
