const initialState = {
    loading: false,
    data: [],
    sound: false
}

const noticeReducer = (state = initialState, action) => {
    switch (action.type){
        case 'CREATE_NOTICE':
            return {
                ...state,
                data: [action.payload, ...state.data]
            };
        case 'GET_NOTICES':
            return {
                ...state,
                data: action.payload
            }
        case 'DELETE_NOTICE':
            return {
                ...state,
                data: action.payload
            }
        default:
            return state;
    }
}


export default noticeReducer;
