import { getAPI, patchAPI } from "../../apis/Axios"
import { imageUpload } from "../../apis/Cloudinary"

export const getUser= (auth, id) => async (dispatch)=> {
    try {
        const res = await getAPI(`/user/${id}`, auth.token);
        dispatch({type:'GET_USER', payload: res.data.user});
        // get posts
        const res2 = await getAPI(`/post/user_posts/${id}`, auth.token);
        dispatch({type:'GET_USER_POSTS', payload: res2.data.posts})

    } catch(err) {
        dispatch({type: 'ALERT', payload: {error: err.response.data.msg}});
    }
}


export const follow = (auth, user) => async (dispatch) => {
    let profileUser = {...user, followers: [...user.followers, auth.user]};

    try {
        const res = await patchAPI(`user/${user._id}/follow`, null, auth.token);

        if(auth.user._id === user._id) dispatch({type: 'FOLLOW' , payload: profileUser});       
        dispatch({type: 'AUTH', payload: {...auth, user: {...auth.user, following: [...auth.user.following, profileUser]}}});
    
    } catch(err) {
        dispatch({type: 'ALERT', payload: {error: err.response.data.msg}});
    }
}

export const unfollow = (auth, user) => async (dispatch) => {
    let  newFollowers = []; 
    for (let i = 0; i < user.followers.length; i++) {
        if(auth.user._id !== user.followers[i]._id) newFollowers.push(user.followers[i]);
    }

    let profileUser = {...user, followers: newFollowers};

    let newFollowing = [];

    for(let i = 0; i < auth.user.following.length; i++) {
        if(profileUser._id !== auth.user.following[i]._id) newFollowing.push(auth.user.following[i]);
    }

    try {
        const res = await patchAPI(`user/${user._id}/unfollow`, null, auth.token);
        if(auth.user._id === user._id) dispatch({type: 'UNFOLLOW' , payload: profileUser});
        // delete profileUser in following
        dispatch({type: 'AUTH', payload: {...auth, user: {...auth.user, following: newFollowing}}});

    } catch(err) {
        dispatch({type: 'ALERT', payload: {error: err.response.data.msg}});
    }
}

export const updateUser = (auth, user, data, image) => async (dispatch) => {
    try {
        let avatar,background;
        if(image.avatar) avatar = await imageUpload([image.avatar]);
        if(image.background) background = await imageUpload([image.background]);

        dispatch({ type: 'ALERT', payload: {loading: true} });
        dispatch({type: 'MODAL' , payload: true});
        const res = await patchAPI(`user/${user._id}`, data, auth.token);
        dispatch({
            type: 'AUTH',
            payload: {
                ...auth,
                user: {
                    ...auth.user, ...userData,
                    avatar: avatar ? media[0].url : auth.user.avatar,
                }
            }
        })
        dispatch({ type: 'MODAL' , payload: false});
        dispatch({ type: 'ALERT', payload: {success: res.data.msg} });
        dispatch({ type: 'ALERT', payload: {} });

    } catch(err) {
        dispatch({ type: 'MODAL' , payload: false});
        dispatch({type: 'ALERT', payload: {error: err.response.data.msg}});
    }
}
