export const addMedia = async (oldFiles, newFiles) => {
    const temp = oldFiles;
    let err = '';

    newFiles.forEach(file =>{
        if(!file) return err='File does not exit';
        if(file.size > 1024*1024*25) return err='File is larger than 25mb';
        return temp.push(file);
    });
    if(err) return {success: '', error: err};
    //  dispatch({ type: 'ALERT', payload: {error: err} })
    // setForm({...form, [e.target.name]: temp});
    // e.target.value=null;
    return {success: temp, error: ''};
}
