import axios from 'axios';

const postLogin = async (bodyFormData) => {
    try {
        const res = await axios.post('http://localhost:5000/api/auth/login',bodyFormData,{
            headers: {'content-type': 'multipart/form-data'}
        });
        if(res.status === 200){
            localStorage.setItem('TOKEN',res.data.accessToken);
        }
        return res;
    } catch(err) {
        return err.response.data;
    }
}

const getLogin = async () => {
    if(localStorage[TOKEN]){
        axios.defaults.headers.common['Authorization'] = `Bearer + ${localStorage[TOKEN]}`;
    }
    try {
        const res = await axios.get('http://localhost:5000/api/auth/login');
        if(res.status === 200) dispatch(authActions.login());
    } catch(err) {
        res.status(400).send(err.message);
    }
}

export {postLogin};
