import React, { useState, useEffect, useRef } from 'react';
import styles from './Status.module.css';
import {MdFormatQuote, MdPhotoCamera, MdRemoveCircle} from 'react-icons/md';
import {RiShareBoxLine} from 'react-icons/ri';
import {ImAttachment} from 'react-icons/im';
import RoundWrapper from '../../components/util/RoundWrapper';
import Card from '../util/Card';
import { useDispatch, useSelector } from 'react-redux';
import {createPost} from '../../store/actions/postAction';

const Status = () => {
    const dispatch = useDispatch();
    const txtarea = useRef();

    const auth = useSelector(state=>state.auth);

    const [form,setForm] = useState({
        body: '',
        image: [],
        file: []
    });

    const inputHandler = (e) => {
        setForm({...form,body: e.target.value});
    }

    useEffect(()=>{
        if(!form.body.trim()) document.getElementById("submit").disabled = true;
        else document.getElementById("submit").disabled = false;
    },[form.body])

    const attachHandler = e => {
        const files = [...e.target.files];
        const filesArr = form.image;
        let err = '';

        files.forEach(file =>{
            if(!file) return err='File does not exit';
            if(file.size > 1024*1024*5) return err='File is larger than 5mb';
            return filesArr.push(file);
        });
        if(err) dispatch({ type: 'ALERT', payload: {error: err} })
        setForm({...form, [e.target.name]: filesArr});
    }
    
    const removeAttachmentHandler = e => {
        const index = e.target.attributes[0].value;
        form.image.splice(index,1);
        setForm({...form});
    }

    const submitHandler = async e => {
        e.preventDefault();

        if(!form) return;
        if(auth.token && form){
            dispatch(createPost(form,auth));
            setForm({
                body: '',
                image: [],
                file: []
            });
        }
    }

    const setStyle = () => {
        // let txt = document.getElementById('textarea');
        // let con = document.getElementById('con');

        // txt.style.height = txt.scrollHeight + "px";
        // con.style.height = txt.scrollHeight + "px";
        txtarea.current.attributes.style = `style="100px"`;
        console.log(txtarea.current.attributes.style)
    }

    return (
        <div id='con' className={styles.container}>
            <ul className={styles.list_items}>
                <li className={styles.item}>
                    <RoundWrapper fontsize="1.5rem" diameter='45px' bgcolor='#EBB35E'>
                        <MdFormatQuote />
                    </RoundWrapper>
                    <div className={styles.text}>Write a quote</div>
                </li>
                <li className={styles.item}>
                    <RoundWrapper fontsize="1.5rem" diameter='45px' bgcolor='#D498E9'>
                        <MdPhotoCamera />
                    </RoundWrapper>
                    <div className={styles.text}>Upload a photo</div>
                </li>
                <li className={styles.item}>
                    <RoundWrapper fontsize="1.5rem" diameter='45px' bgcolor='#7BCDD2'>
                        <RiShareBoxLine />
                    </RoundWrapper>
                    <div className={styles.text}>Share a post</div>
                </li>
            </ul>
            <Card width={'100%'} minheight={form.image.length!==0 ? '150%' : '65%'}>
                <form onSubmit={submitHandler} action="" method="post" className={styles.form}>
                    <textarea ref={txtarea} onInput={setStyle} style={{height: `${form.image.length!==0 ? '35%' : '50%'}`}} onChange={inputHandler} value={form.body}  className={styles.textarea}  spellCheck="false" type="text" name="body" placeholder="What's on your mind?" />
                    <div className={styles.show_images} style={{height: `${form.image.length!==0 ? '35%' : '0%'}`}} >
                        {
                            form.image && (
                                form.image.map((img,index)=>(
                                    <div className={styles.image_wrapper} key={index} >
                                        <img  src={URL.createObjectURL(img)} alt="image" />
                                        <div onClick={removeAttachmentHandler} htmlFor={index} className={styles.remove_btn}><MdRemoveCircle className={styles.icon} /></div>
                                    </div>
                                ))
                            )
                        }
                    </div>
                    <div className={styles.submit_wrapper} style={{height: `${form.image.length!==0 ? '20%' : '30%'}`}}>
                        <div className={styles.attach_wrapper}>
                            <div className={styles.attach}>
                                <div className={styles.file}>
                                    <input onChange={attachHandler} multiple accept="*" className={styles.file_input} type="file" name="file" id="file" />
                                    <label className={styles.label} htmlFor="file"><ImAttachment/></label>
                                    </div>
                                <div className={styles.image}>
                                    <input onChange={attachHandler} multiple accept="image/*" className={styles.file_input} type="file" name="image" id="image" />
                                    <label className={styles.label} htmlFor="image"><MdPhotoCamera/></label>
                                </div>
                            </div>                            
                        </div>
                        <button id='submit' className={styles.submit} type="submit">POST►</button>
                    </div>
                </form>
            </Card>            
        </div>
    )
}

export default Status
