import React from 'react'
import styles from './LoginForm.module.css'
import {AiOutlineUser,AiOutlineLock} from "react-icons/ai";
import BorderButton from '../Util/BorderButton';
import {Link} from 'react-router-dom';
import axios from 'axios';
import {useState} from 'react';
var FormData = require('form-data');

export default function LoginForm() {
    const [loginForm,setLoginForm] = useState({
        username:'',
        password: '',
        remember: false
    });

    const {username,password,remember} = loginForm;

    const loginFormHandler = e => setLoginForm({...loginForm,[e.target.name]: e.target.value});
    const checkboxHandler = () => setLoginForm({...loginForm, remember: !remember});

    const submitHandler = async (e) => {
        var bodyFormData = new FormData();
        bodyFormData.append('username', username);
        bodyFormData.append('password', password);
        bodyFormData.append('remember', remember);
        e.preventDefault();
            try {
                const res = await axios.post('http://localhost:5000/api/auth/login',bodyFormData,{
                    headers: {'content-type': 'multipart/form-data'}
                });
                console.log(res);
                if(res.status == 200) {
                    localStorage.setItem('TOKEN',res.data.accessToken);

                }
            } catch(err) {
                console.log(err.response);
            }
    }
    return (
        <div className={styles.form_wrapper}>
            <form onSubmit={submitHandler} className={styles.form} action="http://localhost:5000/api/auth/login" method="post">
            <div className={styles.title}>Sign in to DevUp</div>
            <div className={styles.inputs_wrapper}>
                <div className={styles.input_wrapper}>
                    <label className={styles.icon_wrapper} htmlFor="username"><AiOutlineUser className={styles.icon}/></label>
                    <input onChange={loginFormHandler} value={username} className={styles.input} type="text" name="username" id="username" placeholder="Username"/>
                </div>
                <div className={styles.input_wrapper}>
                    <label className={styles.icon_wrapper} htmlFor="password"><AiOutlineLock className={styles.icon}/></label>
                    <input onChange={loginFormHandler} value={password} className={styles.input} type="password" name="password" id="password" placeholder="Password"/>
                </div>
            </div>  
            <div className={styles.util_inline}>
                <div className={styles.input_radio_wrapper}>
                    <input  onChange={checkboxHandler} type="checkbox" name="remember" id="remember" />
                    <label className={styles.remember} htmlFor="remember">Remember me</label>
                </div>
                <Link to="/forgot" className={styles.forgot}>Forgot Password?</Link>
            </div>
            <BorderButton type={'submit'} color={'white'} bgcolor={'#487fec'} value={'Sign in'} />
            </form> 
        </div>
    )
}
