import React, { useState,useRef, useEffect } from 'react';
import {useDispatch, useSelector} from 'react-redux';
import styles from './Search.module.css';
import {AiOutlineSearch} from 'react-icons/ai';
import { getAPI } from '../../apis/Axios';
import { motion, AnimatePresence } from "framer-motion"

import { CSSTransition,TransitionGroup } from "react-transition-group";
import UserCard from '../util/UserCard';
import {Link} from 'react-router-dom';
import { searchUser } from '../../store/actions/searchAction';

const Search = () => {
    const dispatch = useDispatch();
    
    const ref = useRef();
    const searchCard = useRef(null);
    const {auth, search} = useSelector(state=>state);

    const [data,setData] = useState('');
    const [users,setUsers] = useState([]);
    const [isShow,setIsShow] = useState(false);

    const variants = {
        initial: {
            maxHeight: 0,
        },
        enter: {
          maxHeight: '800px',
          transition: {
            duration: .3,
            when: "beforeChildren",
          },
        },
        exit: {
            maxHeight: 0,
          transition: { duration: 0.6 },
        }
      }


    useEffect(()=>{
        const checkIfClickOutside = e => {
            if(isShow && ref.current && !ref.current.contains(e.target)) setIsShow(false); 
        }
        document.addEventListener("mousedown", checkIfClickOutside)
        return () => {
            document.removeEventListener("mousedown", checkIfClickOutside)
        }
    },[isShow]);

    useEffect(()=>{
        if(!data) {
            setUsers([]);
            setIsShow(false);
        }
        else submitHandler();
        
    },[data])

    const submitHandler = async () => {
        // e.preventDefault();
        if(!data) return;
        dispatch(searchUser(data, auth))
        setIsShow(true);
    }

    const inputHandler = e => {
        setData(e.target.value);
    }

    return (
        <div ref={ref}  className={styles.search_bar}>
            <div onClick={()=>setIsShow(true)} className={styles.input_wrapper}>
                <label className={styles.icon_wrapper} htmlFor="seacrh"><AiOutlineSearch className={styles.icon}/></label>
                <input spellCheck="false" onChange={inputHandler} autoComplete="off" value={data}  className={styles.input} type="text" name="seacrh" id="seacrh" placeholder="Search for people, jobs, companies and more..."/>
            </div>
            
            <CSSTransition
            in={isShow}
            timeout={500}
            appear
            classNames={{
                enter: styles.enter,
                enterActive: styles.enter_active,
                exit: styles.exit,
                exitActive: styles.exit_active
            }}
            unmountOnExit
            >
                {search.loading ? <div>loading</div>
            :     <div style={{height: `${(50+15*2) * search.users.length}px`}} className={styles.results} >
            <TransitionGroup component={null}>
                {search.users.map(user => 
                    <CSSTransition
                        key={user._id}
                        timeout={500}
                        classNames={{
                            enter: styles.enter2,
                            enterActive: styles.enter_active2,
                            exit: styles.exit2,
                            exitActive: styles.exit_active2,
                        }}
                        unmountOnExit
                        in={isShow}
                    >
                        <div key={user._id} className={styles.item_wrapper} style={{width: '100%', height: height+'px', margin: `${margin}px 0`}} >
                            <Link to={`/profile/${user._id}`} className={styles.item}>
                                <UserCard avatar={user.avatar} username={user.username} />
                            </Link>             
                        </div>  
                    </CSSTransition>   
                )}
            </TransitionGroup>
        </div> 
            }
            
            </CSSTransition>
        </div>
    )
}

export default Search
