import React, { useEffect, useRef, useState } from 'react'
import { Link } from 'react-router-dom'

import styles from './MediaCard.module.css'

const MediaCard = ({post}) => {
    const ref = useRef()

    useEffect(()=>{
        setWidth(ref.current.offsetWidth)
        console.log(ref.current.offsetWidth)
    }, ref)

    const [width, setWidth] = useState(0);
    

    return (
        <Link to={`/post/${post._id}`} >
            <div ref={ref} className={styles.container}>
                <div style={width ? {height: width}:{}} className={styles.image_wrapper}>
                    {
                    post.media[0].url.match(/video/i)
                    ? 
                    <video controls src={post.media[0].url} alt="video" />
                    :
                    <img className={styles.image} src={post.media[0].url} alt='media' />
                    }
                </div>
                <div className={styles.count}>count</div>
                <div className={styles.black_bg}></div>
            </div>
        </Link>
    )
}

export default MediaCard
