import { getAPI } from "../../apis/Axios"

export const getUser= (auth, id) => async (dispatch)=> {
    try {
        dispatch({type: 'ALERT', payload: {loading: true}})
        const res = await getAPI(`/user/${id}`, auth.token);
        console.log(res.data.user)
        dispatch({type:'GET_USER', payload: res.data});
        dispatch({type: 'ALERT', payload: {loading: false}})

    } catch(err) {
        dispatch({type: 'ALERT', payload: {error: err.response.data.msg}});
    }
}

export const follow = (auth, user) => async (dispatch) => {
    try {
        let newUser = {...user, followers: [...user.followers, auth.user]};
        dispatch({type: 'FOLLOW' , payload: newUser});
        console.log('ok');

        dispatch({type: 'AUTH', payload: {...auth, user: {...auth.user, following: [...auth.user.following, newUser]}}});
        console.log('ok');
    
    } catch(err) {

    }
}
