import { UpdateData } from "../EditData";

const initialState = {
    user: {},
    posts: [],
    loading: false
}

const profileReducer = (state = initialState, action) => {
    switch (action.type){
        case 'LOADING_POST':
            return {
                ...state,
                loading: action.payload
            }
        case 'GET_USER':
            return {
                ...state,
                user: action.payload
            };
        case 'FOLLOW':
            return {
                ...state,
                user: action.payload
            };
        case 'UNFOLLOW':
            return {
                ...state,
                user: action.payload
            };
        case 'GET_MORE_POSTS':
            let newPosts = [...state.posts];
            action.payload.map(post=>newPosts.push(post))
            return {
                ...state, 
                posts: newPosts
            }
        case 'GET_POSTS':
            return {
                ...state,
                posts: action.payload
            }
        case 'UPDATE_POST':
            return {
                ...state,
                posts: UpdateData(state.posts, action.payload._id, action.payload)
            }
        default:
            return state;
    }
}


export default profileReducer;
