import LoginForm from "../../components/form/LoginForm";
import RegisForm from "../../components/form/RegisForm";
import SlideForm from "../../components/form/SlideForm";
import styles from "./Auth.module.css";
import Logo from '../../assets/logo.png';
import { useDispatch, useSelector } from 'react-redux';
import {useEffect} from 'react';
import { Link, useHistory } from 'react-router-dom';

const Auth = () => {
    const { auth, modal } = useSelector(state => state)
    const history = useHistory()

    useEffect(() => {
        if(auth.token) history.push("/")
    }, [auth.token, history]);
    

    return (
        <div style={{filter: `blur(${modal? '3px' : '0'})`}} className={styles.body}>
            <div className={styles.wrapper}>
                <div className={styles.container}>
                    <SlideForm  />
                    <div className={styles.login_form_side}>
                        <div className={styles.logo_wrapper}>
                            <img className={styles.logo} alt="logo" src={Logo}/>
                        </div>
                        <LoginForm />
                    </div>
                    <div className={styles.regis_form_side}>
                        <RegisForm/>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Auth;
