import React from 'react'
import { useDispatch, useSelector } from 'react-redux'

import { deleteProject, openProject } from '../../../store/actions/projectAction'

import { AiOutlineEdit, AiOutlineShareAlt, AiOutlineDelete} from 'react-icons/ai'
import {MdOpenInNew} from 'react-icons/md'

import styles from './ProjectCard.module.css'

const ProjectCard = ({project}) => {
    const dispatch = useDispatch()
    const {auth} = useSelector(state=>state)

    const openHandler = () => {
        const body = {
            code: project.code,
            html: project.html,
            css: project.css,
            js: project.js
        }
        dispatch(openProject(body))
    }

    const deleteHandler = () => {
        dispatch(deleteProject(auth, project._id))
    }

    return (
        <div style={{backgroundImage:`url(${project.author.avatar})`}} className={styles.container}>
            <div className={styles.placeholder}></div>
            <div className={styles.content}>
                <div className={styles.infor}>
                    <div className={styles.name}>{project.name}</div>
                    <div className={styles.author}>Author: {project.author.username}</div>
                </div>
                <div className={styles.btn_group}>
                    <div className={styles.icon_wrapper}><AiOutlineEdit/></div>
                    <div onClick={deleteHandler} className={styles.icon_wrapper}><AiOutlineDelete/></div>
                    <div className={styles.icon_wrapper}><AiOutlineShareAlt/></div>
                </div>
            </div>
            <div onClick={openHandler} className={styles.open}><MdOpenInNew/></div>
        </div>
    )
}

export default ProjectCard
