import React, { useEffect, useState } from 'react'
import styles from './DropdownCard.module.css'
import { CSSTransition } from "react-transition-group"

const DropdownCard = (props) => {
    const [isShow, setIsShow] = useState(false);
    
    useEffect(()=>{
        if(props.isShow) setIsShow(true)
    },[props.isShow])

    return (
        <CSSTransition
            timeout={500}
            appear
            classNames={{
                enter: styles.enter,
                enterActive: styles.enter_active,
                exit: styles.exit,
                exitActive: styles.exit_active,
            }}
            unmountOnExit
            
        >
        <div onClick={()=>setIsShow(false)} style={{bottom: props.bottom, right: props.right, width: props.width}} className={styles.container}>
            {props.children}
        </div>
        </CSSTransition>
        
    )
}

export default DropdownCard
