import React, { useEffect, useRef, useState } from 'react'

import Header from '../../components/header/Header'

import { motion } from 'framer-motion'

import Editor from '../../components/code/Editor'

import {SiHtml5, SiCsswizardry, SiJavascript} from 'react-icons/si'

import styles from './Code.module.css'


const Code = () => {
    const constRef = useRef(null);

    const [html, setHtml] = useState('')
    const [css, setCss] = useState('')
    const [js, setJs] = useState('')
    const [srcDoc, setSrcDoc] = useState('');

    useEffect(() => {
        const timeout = setTimeout(() => {
          setSrcDoc(`
            <html>
              <body>${html}</body>
              <style>${css}</style>
              <script>${js}</script>
            </html>
          `)
        }, 500)
        return () => clearTimeout(timeout)
    }, [html, css, js])

    return (
        <>
            <Header/>
            {/* <motion.div style={{width: 500, height:500}} ref={constRef}>
                <motion.div
                drag
                dragConstraints={constRef}
                style={{width: 'fit-content'}}
                >
                    drag me
                </motion.div>
            </motion.div> */}
            <div className={styles.container}>
                <div>
                    
                </div>
                <div className={styles.editors_container}>
                    <Editor
                    language="xml"
                    name="HTML"
                    value={html}
                    onChange={setHtml}
                    labelColor='#d94924'
                    icon={<SiHtml5 />}
                    />
                    
                    <Editor
                    language="css"
                    name="CSS"
                    value={css}
                    onChange={setCss}
                    labelColor='#5bb7ed'
                    icon={<SiCsswizardry/>}
                    />

                    <Editor
                    language="javascript"
                    name="JS"
                    value={js}
                    onChange={setJs}
                    labelColor='#e4d04b'
                    icon={<SiJavascript/>}
                    />
                </div>
                <div className={styles.result}>
                    <iframe
                    srcDoc={srcDoc}
                    title="output"
                    sandbox="allow-scripts"
                    frameBorder="0"
                    width="100%"
                    height="100%"
                    />
                </div>
            </div>
        </>
    )
}

export default Code
