import React from 'react';
import styles from './Header.module.css';
import {AiOutlineSearch,AiOutlineSetting} from 'react-icons/ai';
import RoundWrapper from '../../components/util/RoundWrapper';
import NotificationCount from '../../components/NotificationCount';
import Logo from '../../assets/logo.png';
import Modal from './Modal';
import {useState,useRef,useEffect} from 'react';

function Header() {
    const ref = useRef();

    const [isShow,setIsShow] = useState({
        setting: false,
        noti: false
    });

    useEffect(()=>{
        // Function to check if click outside the object
        const checkIfClickOutside = e => {
            if(isShow && ref.current && !ref.current.contains(e.target)) setIsShow(false);
        }
        // Set event 'when click' trigger
        document.addEventListener("mousedown", checkIfClickOutside)
        return () => {
            // Clean the event
            document.removeEventListener("mousedown", checkIfClickOutside)
        }
    },[isShow]);

    const showSettingToggle = () => {
        setIsShow({setting: !isShow.setting, noti: false});
    }

    const showNotiToggle = () => {
        setIsShow({setting: false, noti: !isShow.noti});
    }

    return (
        <div className={styles.header}>
                <div className={styles.container}>
                    <div className={styles.logo_wrapper}> <img className={styles.logo} src={Logo} alt='logo'/> </div>
                    <div className={styles.search_bar}>
                    <div className={styles.input_wrapper}>
                        <label className={styles.icon_wrapper} htmlFor="seacrh"><AiOutlineSearch className={styles.icon}/></label>
                        <input className={styles.input} type="text" name="seacrh" id="seacrh" placeholder="Search for people, jobs, companies and more..."/>
                    </div>
                    </div>
                    <div className={styles.account}>
                        <div className={styles.avatar}>
                            <RoundWrapper diameter='60px'>
                                <img src={Logo} alt="avatar"/>
                            </RoundWrapper>
                        </div>
                        <div ref={ref} Func={showNotiToggle} className={styles.notification_count}>
                            <NotificationCount bgcolor='#5BB7ED' color='white' />
                            {isShow.noti && <Modal/>}
                        </div>          
                        <div ref={ref} className={styles.setting}>
                            <RoundWrapper Func={showSettingToggle} diameter='40px' bgcolor='#EAEAF1'>
                                <AiOutlineSetting />
                            </RoundWrapper>           
                            {isShow.setting && <Modal/>}
                        </div>
                    </div>
                </div>
            </div>
    )
}

export default Header
