import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { follow, unfollow } from '../../store/actions/profileAction';

const FollowBtn = (props) => {
    const dispatch = useDispatch();
    const [followed, setFollowed] = useState(false);

    const auth = useSelector(state=>state.auth);
    const profile = useSelector(state=> state.profile);

    const [load, setLoad] = useState(false)

    useEffect(()=>{
        if(auth.user.following.find(item => item._id === props.user._id)){
            setFollowed(true);
            console.log('true')
            console.log(auth.user.following)
        }
        else return () => setFollowed(false)
    
    },[auth.user.following, profile.user._id])

    const followHandler = () => {
            setFollowed(true);
            dispatch(follow(auth, profile.user));    
    }

    const unfollowHandler = () => {
        setFollowed(false);
        dispatch(unfollow(auth, profile.user));
        console.log('unfl')       
    }

    return (
        <>
        {followed ? 
            <button onClick={unfollowHandler} >
                <div className="btn">Unfollow</div>
            </button> :
            <button onClick={followHandler} >
                <div className="btn">Follow</div>
            </button>
        }
        </>     
    )
}

export default FollowBtn
