const initialState ={
    isWrongPw: false
}

const userReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'PASSWORD':
            return action.payload

        case 'GET_MORE_POSTS':
            let newPosts = [...state.posts];
            action.payload.map(post=>newPosts.push(post))
            return {
                ...state, 
                posts: newPosts
            }
            
        default:
            return state
    }
}

export default userReducer
