const initialState = {
    loading: false,
    posts: []
}

const postReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'CREATE_POST':
            return {
                ...state,
                posts:[action.payload, ...state.posts]
            }
        case 'GET_POSTS':
            return {
                ...state, 
                posts: action.payload
            }
        case 'UPDATE_POST':
            return {
                ...state,
                posts: action.payload
            }
        default:
            return state;
    }
}

export default postReducer;
