import React, { useEffect, useRef, useState } from 'react'
import styles from './NotificationDropdown.module.css'
import DropdownCard from '../util/DropdownCard'
import {CgMoreAlt} from 'react-icons/cg'
import { useSelector } from 'react-redux'
import NoticeCard from './notices/NoticeCard'
import NotificationOption from './NotificationOption'
import { motion, AnimatePresence } from "framer-motion"


const NotificationDropdown = ({isShow}) => {
    const ref = useRef();
    
    const {notice} = useSelector(state=>state);
    const [isShowOption, setShowOption] = useState(false);

    useEffect(()=>{
        // Function to check if click outside the object
        const checkIfClickOutside = e => {
            if(isShowOption && ref.current &&!ref.current.contains(e.target)) setShowOption(false); 
        }
        // Set event 'when click' trigger
        document.addEventListener("mousedown", checkIfClickOutside)
        return () => {
            // Clean the event
            document.removeEventListener("mousedown", checkIfClickOutside)
        }
    },[isShowOption]);

    const variants = {
        initial: {
            transform: 'translate(150%,0)',
        }
    }

    return (
        <DropdownCard isShow={isShow} bottom={'-50%'} right={'200%'} width={'450px'}>
            <div className={styles.container}>
                <div className={styles.heading}>
                    <div className={styles.title}>Notification</div>
                    <div ref={ref} style={{opacity: `${isShowOption ? '1' : '.5'}`}} onClick={()=>setShowOption(!isShowOption)} className={styles.heading_option}>
                        <CgMoreAlt className={styles.icon}/>
                        {isShowOption && <NotificationOption/>}
                    </div>
                </div>
                <div className={styles.content}>
                <AnimatePresence >
                    {notice.data.length>0 ? notice.data.map((msg,index)=>(
                        <motion.div
                        variants ={variants}
                        drag
                        key={index}
                        initial='initial'
                        animate={{transform: 'translate(0,0)', transitionDuration: `.4s`, transitionDelay:`${(index+1)*0.1}s`}}
                        >
                            <NoticeCard msg={msg} />
                        </motion.div>
                    )) : 
                    <div style={{textAlign: 'center'}}>No notifications available!</div>                   
                    }
                </AnimatePresence>
                    {/* // {notice.data.length>0 ? notice.data.map(msg=>(

                    //     <NoticeCard msg={msg} />
                    // )):
                    // <div style={{textAlign: 'center'}}>No notifications available!</div>
                    // } */}
                </div>
            </div>
        </DropdownCard>
    )
}

export default NotificationDropdown
