import React, { useState } from 'react'
import ReactDOM from 'react-dom'
import { useDispatch, useSelector } from 'react-redux'

import LikeModal from '../modal/LikeModal'
import CommentModal from '../modal/CommentModal'

import styles from './PostCounter.module.css'
import Modal from '../util/Modal'

const PostCounter = ({post}) => {
    const portal = document.getElementById('portal');

    const [isShowLike, setShowLike] = useState(false);
    const [isShowComment, setShowComment] = useState(false);
    
    return (
        <div className={styles.container}>
            {post.likes.length>0 && <div onClick={()=>setShowLike(true)} className={styles.btn}>{`${post.likes.length} likes`}</div>}
            {post.comments.length>0 && <div onClick={()=>setShowComment(true)} className={styles.btn}>{`${post.comments.length} comments`}</div>}

            {isShowLike && ReactDOM.createPortal(
            <Modal setIsEdit={setShowLike} width='30%' height='50%' >
                <LikeModal likes={post.likes} setIsEdit={setShowLike} />
            </Modal>
            , portal)}
            {isShowComment && ReactDOM.createPortal(
            <Modal setIsEdit={setShowComment} width='30%' height='50%' >
                <LikeModal comments={post.comments} setIsEdit={setShowComment} />
            </Modal>
            , portal)}
        </div>
    )
}

export default PostCounter
