import LoginForm from "../components/auth/LoginForm";
import RegisForm from "../components/auth/RegisForm";
import SlideForm from "../components/auth/SlideForm";
import styles from "./Auth.module.css";
import {useState,useEffect} from 'react';
import Logo from '../assets/logo.png';

const Auth = (props) => {
    const [isLogin,setIsLogin] = useState(props.authRoute);

    useEffect( ()=>{
        slideClickHandler();
    },[]);

    function slideClickHandler() {}{
        setIsLogin(!isLogin);
    }

    return (
        <>
        <div className={styles.body}>
            <div className={styles.wrapper}>
                <div className={styles.container}>
                    <SlideForm onClick={slideClickHandler} isLogin={isLogin}/>
                    <div className={styles.login_form_side}>
                        <div className={styles.logo_wrapper}>
                            <img className={styles.logo} alt="logo" src={Logo}/>
                        </div>
                        <LoginForm/>
                    </div>
                    <div className={styles.regis_form_side}>
                        <RegisForm/>
                    </div>
                </div>
            </div>
        </div>
        </>
    );
}

export default Auth;
