import styles from './CommentOption.module.css'
import ToggleCard from '../../../components/util/ToggleCard';

const CommentOption = ({setIsEdit, post}) => {
    const dispatch = useDispatch();
    const {auth, profile} = useSelector(state=>state);

    return (
        <ToggleCard>
            <div className={styles.option_item} onClick={()=>console.log('dog')} >
                Edit
            </div>
            <div className={styles.option_item} onClick={()=>setIsEdit(true)}>
                Delete
            </div>
        </ToggleCard>       
    )
}

export default CommentOption
