import React from 'react'
import {useState, useRef, useEffect} from 'react'
import {Link} from 'react-router-dom'

import {IoSearch} from 'react-icons/io5'

import NotificationDropdown from '../notification/NotificationDropdown'
import NotificationCount from '../notification/NotificationCount'
import RsMenu from './RsMenu'
import SearchResult from './SearchResult'

import { motion, AnimatePresence } from "framer-motion"

import Logo from '../../assets/logo.png'
import styles from './RsHeader.module.css'
import RoundWrapper from '../util/RoundWrapper'

const RsHeader = () => {
    const ref = useRef();

    const [isShow,setIsShow] = useState({
        setting: false,
        noti: false
    });

    const [showNav, setShowNav] = useState(false)
    const [showSearch, setShowSearch] = useState(false)

    useEffect(()=>{
        const checkIfClickOutside = e => {
            if(showNav && ref.current && !ref.current.contains(e.target)) setShowNav(false);   
        }
        document.addEventListener("mousedown", checkIfClickOutside)
        return () => {
            document.removeEventListener("mousedown", checkIfClickOutside)
        }
    },[showNav]);

    const showSettingToggle = () => {
        if(!isShow.setting) setIsShow({noti: false, setting: true});
        else 
        setIsShow({setting: false, noti: false});
    }

    const showNotiToggle = () => {
        if(!isShow.noti) setIsShow({setting: false, noti: true});
        else setIsShow({setting: false, noti: false});
    }



    return (
        <div className={styles.header}>
            <div className={styles.container}>
                <div className={styles.logo_wrapper}>
                    <Link to={'/'} >
                        <img className={styles.logo} src={Logo} alt='logo'/>
                    </Link>
                </div>               
                <div className={styles.btn_group_wrapper}>
                    <div className={styles.btn_group}>
                        <div onClick={()=>setShowSearch(!showSearch)} className={styles.search}>
                            <RoundWrapper diameter='1.2rem'>
                                <IoSearch/>
                            </RoundWrapper>       
                        </div>
                        <NotificationCount margin='1.2rem' diameter='1.7rem' Func={showNotiToggle} bgcolor='#5BB7ED' color='white' />
                        <div onClick={()=>setShowNav(!showNav)} className={styles.menu}>
                            <div className={styles.menu_icon}>
                                <div style={showNav ? {left: '-100%', opacity: 0} : {}} className={styles.line_1}></div>
                                <div style={showNav ? {transformOrigin: 'left', transform: 'rotateZ(45deg)'} : {}} className={styles.line_2}></div>
                                <div style={showNav ? {transformOrigin: 'left', transform: 'rotateZ(-45deg)'} : {}} className={styles.line_3}></div>

                            </div>
                        </div>
                    </div>
                </div>              
            </div>
            <AnimatePresence >
                {showNav &&
                <motion.div 
                ref={ref}
                initial={{transform: 'translate(50%,0)'}}
                animate={{transform: 'translate(0,0)', transitionDuration: `.2s`}}
                exit={{transform: 'translate(50%,0)'}}
                >
                    <RsMenu/>
                </motion.div>}
                {showSearch &&
                <motion.div 
                // ref={ref}
                initial={{transform: 'translate(0,-100%)'}}
                animate={{transform: 'translate(0,0)', transitionDuration: `.2s`}}
                exit={{transform: 'translate(0,-100%)'}}
                className={styles.result}
                >
                    <SearchResult/>
                </motion.div>}
            </AnimatePresence >
            {/* <div className={styles.noti}> */}
                <NotificationDropdown isShow={isShow.noti} />
            {/* </div> */}
        </div>
    )
}

export default RsHeader
