import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Link } from 'react-router-dom'

import { getSuggestion } from '../../../store/actions/suggestionAction'
import FriendCard from '../../profile/Tab/FriendCard'
import Carousel from 'react-multi-carousel'
import 'react-multi-carousel/lib/styles.css'

import styles from './ResponsiveFriendSuggestion.module.css'

const ResponsiveFriendSuggestion = () => {
    const dispatch = useDispatch()
    
    const {suggestion, auth} = useSelector(state=>state)

    useEffect(()=>{
        dispatch(getSuggestion(auth, 1))
        console.log(suggestion)

    },[auth.user._id, dispatch])

    const responsive = {
        superLargeDesktop: {
          breakpoint: { max: 4000, min: 3000 },
          items: 1
        },
        desktop: {
          breakpoint: { max: 3000, min: 1024 },
          items: 1
        },
        tablet: {
          breakpoint: { max: 1024, min: 464 },
          items: 5
        },
        mobile: {
          breakpoint: { max: 464, min: 0 },
          items: 3
        }
      }


    return (
        <div className={styles.container}>
            <Carousel 
            removeArrowOnDeviceType={["tablet", "mobile"]} 
            dotListClass="custom-dot-list-style" 
            draggable={false}  
            swipeable={true}
            responsive={responsive}
            itemClass={styles.carousel_item}
            className={styles.images}>
                {suggestion.users.map((user,index)=>(
                    <Link key={index} to={``} >
                        <FriendCard user={user} />
                        alo      
                    </Link>
                ))}
            </Carousel>{suggestion.users.map((user,index)=>(
                    <Link key={index} to={``} >
                        <FriendCard user={user} />
                        alo      
                    </Link>
                ))}
        </div>
    )}

export default ResponsiveFriendSuggestion
