import { deleteAPI, postAPI } from "../../apis/Axios";

export const createComment = (auth, newComment, post, posts) => async (dispatch) => {
    // logic
    const newPost = {...post, comments: [...post.comments, newComment]};
    let newPosts = [];

    for (let i = 0; i<posts.length; i++) {
        if(posts[i]._id === post._id) newPosts.push(newPost);
        else newPosts.push(posts[i]);
    }
    dispatch({type: 'UPDATE_POST', payload: newPosts});

    try {
        const data = {...newComment, postId: post._id, postUserId: post.user._id}
        const res = await postAPI('comment', data, auth.token);

    } catch(err) {
        dispatch({type: 'ALERT', payload: {error: err.response.data.msg}});
    }
}

export const deleteComment = (auth, comment, post, posts) => async (dispatch) => {
    try {
        // delete the main and reply comments
        const deleteArr = [...post.comments.filter(cmt=>cmt.reply===comment._id), comment];
        // logic
        const newPost = {...post, comments: post.comments.filter(cmt=>!deleteArr.find(deletedCmt=>cmt._id===deletedCmt._id))};
        let newPosts = [];

        for (let i = 0; i<posts.length; i++) {
            if(posts[i]._id !== post._id) newPosts.push(posts[i]);
        }
        dispatch({type: 'UPDATE_POST', payload: newPosts});

        deleteArr.forEach(item=>{
            deleteAPI(`comment/${item._id}`, auth.token);
        })

    } catch(err) {
        dispatch({type: 'ALERT', payload: {error: err.response.data.msg}});
    }
}
