import axios from 'axios';

const postAPI = async (url, data, token) => {
    const res = await axios.post(`/api/${url}`, data, {
        headers: { Authorization: token}
    });
    return res;
}

const getAPI = async (url, token) => {
    const res = await axios.get(`/api/${url}`, {
        headers: {Authorization: token}
    });
    return res;
}

const putAPI = async (url, data, token) => {
    const res = await axios.put(`/api/${url}`, data, {
        headers: {Authorization: token}
    });
    return res;
}

const patchAPI = async (url, data, token) => {
    const res = await axios.patch(`/api/${url}`, data, {
        headers: {Authorization: token}
    });
    return res;
}

export {postAPI,getAPI};
