const initialState = {
    user: {}
}

const profileReducer = (state = initialState, action) => {
    switch (action.type){
        case 'GET_USER':
            return action.payload;
        case 'FOLLOW':
            return action.payload.user;
        default:
            return state;
    }
}


export default profileReducer;
