import React from 'react'
import { useDispatch, useSelector } from 'react-redux'

import {RiDeleteBinLine} from 'react-icons/ri'
import {GoCheck} from 'react-icons/go'

import DropdownCard from '../../util/DropdownCard'
import { deleteNotice, readNotice, unreadNotice } from '../../../store/actions/noticeAction'

import styles from './NoticeOption.module.css'

const NoticeOption = ({msg, isShow, setExiting, bottom, right}) => {
    const dispatch = useDispatch();
    const {auth, notice} = useSelector(state=>state);

    const markHandler = () => {
        if(msg.isRead) dispatch(unreadNotice(auth, msg, notice.data))
        else dispatch(readNotice(auth, msg, notice.data))
    }

    const deleteHandler = () => {
        setExiting(true);
        dispatch(deleteNotice(auth, msg, notice.data))
    }
    
    return (
        <DropdownCard isShow={isShow} right={right+40} bottom={-1*(bottom-40)} >
            <div onClick={markHandler} className={styles.option_item}  >
                <GoCheck className={styles.icon}/>
                <div className={styles.text}>{msg.isRead ? `Mark unread` : `Mark read`}</div>
            </div>      
            <div onClick={deleteHandler} className={styles.option_item} >
                <RiDeleteBinLine className={styles.icon}/>
                <div className={styles.text}>Delete</div>
            </div>
        </DropdownCard>
    )
}

export default NoticeOption
