import React, { useEffect, useRef, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Link, useLocation  } from 'react-router-dom'
import {useHistory} from 'react-router'

import {GrFormClose} from 'react-icons/gr'

import { readNotice } from '../../store/actions/noticeAction'

import RoundWrapper from '../util/RoundWrapper'
import TagIcon from '../util/TagIcon'

import styles from './NotificationDisplay.module.css'
import moment from 'moment'

const NotificationDisplay = () => {
    const ref = useRef();
    const history = useHistory();
    const dispatch = useDispatch();
    const location = useLocation();

    const {notice, auth} = useSelector(state=>state);
    const [isShow, setIsShow] = useState(false);
    const [msg, setMsg] = useState({});

    useEffect(()=>{
        if(!notice.popup) return;
        setIsShow(true);
        setMsg(notice.data[0]);

    },[notice])

    useEffect(()=>{
        isShow && setTimeout(() => {
            closeHandler()
        }, 5000);
    },[isShow])

    const readHandler = () => {
        // dispatch(readNotice(auth, msg))
        if(location.pathname !== `/post/${msg.url}`) history.push(`/post/${msg.url}`);

    }

    const closeHandler = () => {
        // ref.current.style.left = '-20%';
        // setTimeout(() => {
        //     dispatch({type:'SHOW_NOTICE', payload: false})
        //     setIsShow(false)
        // }, 300);
    }

    return (
        <>
            {isShow && 
            <div ref={ref} className={styles.notice_wrapper}>
                <div className={styles.container}>
                    <div className={styles.heading}>
                        <div className={styles.title}>New Notification</div>
                        <div onClick={closeHandler} className={styles.close}><GrFormClose className={styles.icon} /></div>
                    </div>
                    {/* <Link  to={`post/${msg.url}`} replace={true} > */}
                    <div onClick={readHandler} className={styles.content}>
                        <div className={styles.avatar}>
                            <RoundWrapper margin='0 .7rem 0 0' diameter='60px'>
                                <img src={msg.user.avatar} alt='avatar'/>
                                <div className={styles.tag_icon_wrapper}>
                                    <TagIcon tag={msg.tag} />
                                </div>
                            </RoundWrapper>
                            
                        </div>
                        <div className={styles.body}>
                            <div className={styles.text_noti}>
                                <b>{msg.user.username}</b> {msg.text}
                            </div>
                            <div className={styles.time}>{moment(msg.createdAt).fromNow()}</div>
                        </div>
                        {msg.image && <div className={styles.image_wrapper}><img src={msg.image}/></div>}
                    </div>
                    {/* </Link> */}
                </div>
            </div>
            }
        </>
    )
}

export default NotificationDisplay
