import { DeleteData } from "../EditData"

const initialState = {
    loading: false,
    savedPosts: []
}

const savedReducer = (state=initialState, action) => {
    switch (action.type) {
        case 'LOADING_SAVED':
            return {
                ...state,
                loading: action.payload
            }
        case 'ADD_SAVED':
            return {
                ...state,
                savedPosts: action.payload
            }
        case 'DELETE_SAVED':
            return {
                ...state,
                savedPosts: DeleteData(state.savedPosts, action.payload._id)
            }       
        default: return state
    }
}

export default savedReducer
