import React, {useState } from 'react'
import {useSelector, useDispatch} from 'react-redux'
import Logo from '../../../assets/logo.png'
import RoundWrapper from '../../util/RoundWrapper'
import {createComment} from '../../../store/actions/commentAction'
import styles from './InputComment.module.css'

const InputComment = ({onReply, setOnReply, post}) => {
    const dispatch = useDispatch();
    const [body, setBody] = useState('');
    const {auth} = useSelector(state=>state);

    const submitHandler = () => {
        const newComment = {
            body,
            user: auth.user,
            createdAt: new Date().toISOString(),
            reply: onReply && onReply.commentId,
            tag: onReply && onReply.user
        }
        dispatch(createComment(auth, newComment, post))
    }

    const inputHandler = (e) => {
        if(e.keyCode == 13 && e.shiftKey == false){
            e.preventDefault();
            submitHandler();
        } 
        setBody(e.target.value);
    }
    
    return (
        <form onSubmit={submitHandler} className={styles.container}>
            <div className={styles.avatar}>
                <RoundWrapper diameter='50px' >
                    <img src={Logo} />
                </RoundWrapper>
            </div>
            <div className={styles.input_wrapper}>
                <input onChange={inputHandler} value={body} spellCheck="false" rows='1' type="text" name="body" placeholder="Write your comment here..." className={styles.input} />
            </div>
        </form>
    )
}

export default InputComment
