import React, { useState,useRef, useEffect } from 'react';
import {useDispatch, useSelector} from 'react-redux';
import styles from './Search.module.css';
import {AiOutlineSearch} from 'react-icons/ai';
import { getAPI } from '../../apis/Axios';
import { CSSTransition,TransitionGroup } from "react-transition-group";
import UserCard from './UserCard';

const Search = () => {
    const dispatch = useDispatch();
    const ref = useRef();
    const auth = useSelector(state=>state.auth);

    const [search,setSearch] = useState('');
    const [users,setUsers] = useState([]);
    const [isShow,setIsShow] = useState(false);

    const height = 50;
    const margin = 10;

    useEffect(()=>{
        // Function to check if click outside the object
        const checkIfClickOutside = e => {
            if(isShow && ref.current && !ref.current.contains(e.target)) setIsShow(false); 
        }
        // Set event 'when click' trigger
        document.addEventListener("mousedown", checkIfClickOutside)
        return () => {
            // Clean the event
            document.removeEventListener("mousedown", checkIfClickOutside)
        }
    },[isShow]);

    useEffect(()=>{
        if(!search) {
            setUsers([]);
            setIsShow(false);
        }
    },[search])

    const submitHandler = async e => {
        e.preventDefault();

        if(!search) return;
        if(auth.token && search){
            try {
                const res = await getAPI(`user/search?username=${search}`, auth.token);
                setUsers(res.data.user);
                setIsShow(true);
            } catch (err) {
                dispatch({type: 'ALERT', payload: {error: err.response.data.msg}})
            }   
        }
    }

    const inputHandler = e => {
        setSearch(e.target.value);
    }

    return (
        <form ref={ref} onSubmit={submitHandler} className={styles.search_bar}>
            <div onClick={()=>setIsShow(true)} className={styles.input_wrapper}>
                <label className={styles.icon_wrapper} htmlFor="seacrh"><AiOutlineSearch className={styles.icon}/></label>
                <input onChange={inputHandler} autoComplete="off" value={search}  className={styles.input} type="text" name="seacrh" id="seacrh" placeholder="Search for people, jobs, companies and more..."/>
            </div>
            <CSSTransition
            in={isShow}
            timeout={500}
            appear
            classNames={{
                enter: styles.enter,
                enterActive: styles.enter_active,
                exit: styles.exit,
                exitActive: styles.exit_active
            }}
            unmountOnExit
            >
            <div style={{height: `${(height+margin*2) * users.length}px`}} className={styles.results} >
                <TransitionGroup>
                <div className={styles.list_items}>
                    { users.map(user => 
                        <CSSTransition
                            key={user.id}
                            timeout={500}
                            classNames={{
                                enter: styles.enter2,
                                enterActive: styles.enter_active2,
                                exit: styles.exit2,
                                exitActive: styles.exit_active2,
                            }}
                            unmountOnExit
                            in={isShow}
                        >
                            <div className={styles.item} style={{width: '100%', height: height+'px', margin: `${margin}px 0`}} >
                                <UserCard key={user.id} avatar={user.avatar} username={user.username} />
                            </div>  
                        </CSSTransition>   
                    )}
                </div>
                </TransitionGroup>
            </div> 
            </CSSTransition>
        </form>
    )
}

export default Search
