import {getAPI, postAPI} from '../../apis/Axios';
import {imageUpload} from '../../apis/Cloudinary';

export const createPost = (data,auth) => async (dispatch) => {
    let media = [];
    try {
        dispatch({type: 'ALERT', payload: {loading: true}});
        if(data.image.length > 0) media = await imageUpload(data.image);

        const res = await postAPI('post', {body: data.body,image: media}, auth.token);
        // dispatch({type:'CREATE_POST'});
        dispatch({type: 'ALERT', payload: {success: res.data.msg}});

    } catch(err) {
        dispatch({type: 'ALERT', payload: {error: err.response.data.msg}})
    }
}

export const getPosts = (token) => async (dispatch) => {
    try {
        const res = await getAPI('post', token);
        dispatch({type: 'GET_POST', payload: res.data})
        // console.log(res.data);

    } catch(err) {
        dispatch({type: 'ALERT', payload: {error: err.response.data.msg}})
    }
}
