import React, { useEffect,useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import Card from '../../components/util/Card'
import { getPosts } from '../../store/actions/postAction'
import RoundWrapper from '../util/RoundWrapper'
import {BsFillChatDotsFill} from 'react-icons/bs';
import {IoMdShareAlt} from 'react-icons/io';
import {AiTwotoneHeart} from 'react-icons/ai';
import styles from './PostCard.module.css'

const PostCard = ({post}) => {
    // const dispatch = useDispatch();
    // const auth = useSelector(state=>state.auth);

    // const [post,setPost] = useState({});

    // useEffect(() => {
    //     // dispatch(getPosts(auth.token));
    //     console.log('post')

    //     setPost(props);
    // })


    return (
        <Card width='100%' height='auto' minheight='60%' bg='#FAFBFC' mg='10% 0'>
            <div className={styles.absolute_wrapper}>
                <RoundWrapper diameter='90px' >
                    <img src={post.user.avatar} />
                </RoundWrapper>
            </div>
            <div className={styles.otpion}>
                <div className="save"></div>
                <div className="edit"></div>
                <div className="delete"></div>
            </div>
            {/*  */}
            <div className={styles.content_wrapper }>
                <div className={styles.content}>
                    <div className={styles.intro}>
                        <div className={styles.name}>Vu Hoang Minh</div>
                        <div className={styles.time}> - 3d</div>
                    </div>
                    <div className={styles.text}>This is content  lorem abuh ckmsmm cnhe nasjk lorem abuh ckmsmm cnhe nasjk</div>
                    <div className="images">this is pic</div>
                </div>
            </div>
            {/*  */}
            <div className={styles.react}>
                <div className={styles.react_btn}>
                    <div className={styles.icon_wrapper}><AiTwotoneHeart/></div>
                    <div className={styles.label}>Like</div>        
                </div>
                <div className={styles.react_btn}>
                    <div className={styles.icon_wrapper}><BsFillChatDotsFill/></div>
                    <div className={styles.label}>Comment</div>            
                </div>
                <div className={styles.react_btn}>
                    <div className={styles.icon_wrapper}><IoMdShareAlt/></div>
                    <div className={styles.label}>Share</div>         
                </div>
            </div>
            {/*  */}
            <div className={styles.comments}></div>
            <div className={styles.reply}></div>
        </Card>
    )
}

export default PostCard
